import classes from "./AvailableMeals.module.css";
import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Sushi",
    description: "Classic Japanese Speciality!",
    price: 22.99,
  },
  {
    id: "m2",
    name: "Schnitzel",
    description: "Classic German Speciality!",
    price: 16.5,
  },
  {
    id: "m3",
    name: "Barbecue Burger",
    description: "Slow BBQ Cooked, Angus Beef Burger!",
    price: 12.99,
  },
  {
    id: "m4",
    name: "Poke Bowl",
    description: "Healthy Poke Bowl Full of Goodness!",
    price: 18.99,
  },
];

const AvailableMeals = () => {
  const mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
        id
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));
  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
